//all the alcohol to be used to load drinks
//map alcohol.js in a grid
import React from 'react'
import Alcohol from "./Alcohol"

const AlcoholList = ({alcohols}) => {
  const {drinks} = alcohols;

    function renderAlcohol(){
      if(!!drinks){
        const {ing} = drinks;
        console.log(drinks);
        const alcohol = drinks.filter((drink) => {return drink.strIngredient1 == "Gin" || drink == "Light rum"});
        console.log(alcohol);
        return drinks.map((alcohol) => {return (
            <Alcohol alcohol={alcohol.strIngredient1} key={alcohol.strIngredient1}/>
        )})
      }
    }

  return (
    <div>{renderAlcohol()}</div>
  )
}

export default AlcoholList
