//all the alcohol to be used to load drinks
//map alcohol.js in a grid
import React from 'react'
import Alcohol from "./Alcohol"

const AlcoholList = ({alcohols}) => {
  const {drinks} = alcohols;

    function renderAlcohol(){
      if(!!drinks){
        console.log(drinks);
        const alcoholic = drinks.filter((drink) => {return drink.strIngredient1 == "Gin" || drink == "Dark rum" || drink == "Vodka" || drink == "Tequila" || drink == "Bourbon" || drink == "Scotch" || drink == "Brandy" || drink == "Champagne" || drink == "Red wine" || drink == "Sherry" || drink == "Cognac" || drink == "Everclear" || drink == "Lager" || drink == "Whiskey" || drink == "Ale"});
        console.log(alcohol);
        return alcoholic.map((alcohol) => {return (
            <Alcohol alcohol={alcohol.strIngredient1} key={alcohol.strIngredient1}/>
        )})
      }
    }

  return (
    <div>{renderAlcohol()}</div>
  )
}

export default AlcoholList
