//each individual drink, probably just the name
//onClick toggle state to show DrinkDetails
import React, {useState, useEffect} from 'react'

const Drink = ({drink}) => {
  const [drinkDetails, setDrinkDetails] = useState([]);
  useEffect(() => {
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${drink.idDrink}`)
    .then(r => r.json())
    .then(drinkData => setDrinkDetails(drinkData.drinks))
  }, [])

  console.log(drinkDetails)

  return (
    <div>
      <img src={drinkDetails.strDrinkThumb} alt={drinkDetails.streDrink}/>
      <ul>Ingredients :</ul>
      {drinkDetails.strIngredient1 ? <li>{drinkDetails.strIngredient1}</li> : null}
    </div>
  )
}

export default Drink
