//each individual drink, probably just the name
//onClick toggle state to show DrinkDetails
import React, {useState, useEffect} from 'react'

const Drink = ({drink}) => {
  const [drinkDetails, setDrinkDetails] = useState([]);
  useEffect(() => {
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${drink.idDrink}`)
    .then(r => r.json())
    .then(drinkData => setDrinkDetails(drinkData.drinks[0]))
  }, [])

  console.log(drinkDetails)

  return (
    <div>
      <img src={drinkDetails.strDrinkThumb} alt={drinkDetails.streDrink}/>
      <ul>Ingredients :</ul>
      {drinkDetails.strIngredient1 ? <li>{drinkDetails.strIngredient1}</li> : null}
      {drinkDetails.strIngredient2 ? <li>{drinkDetails.strIngredient2}</li> : null}
      {drinkDetails.strIngredient3 ? <li>{drinkDetails.strIngredient3}</li> : null}
      {drinkDetails.strIngredient4 ? <li>{drinkDetails.strIngredient4}</li> : null}
      {drinkDetails.strIngredient5 ? <li>{drinkDetails.strIngredient5}</li> : null}
      {drinkDetails.strIngredient6 ? <li>{drinkDetails.strIngredient6}</li> : null}
      {drinkDetails.strIngredient7 ? <li>{drinkDetails.strIngredient7}</li> : null}

    </div>
  )
}

export default Drink
